svg.script:
        var svgDoc;
        var dotSpace = 20;
        var activeDots = {"dot-1":40,"dot-2":80,"dot-3":120,"dot-4":160,"dot-5":200,"dot-6":240,"dot-7":280,"dot-8":320,"dot-9":360,"dot-10":400,"dot-11":440,"dot-12":480,"dot-13":520};
        var selectedPoint = null;
        var drawnPoints = {};
        function init(evt) {
            if ( window.svgDocument == null ) {
                svgDoc = evt.target.ownerDocument;
                var mainDocument = window.parent.document;
            }  

            for (dot in activeDots){
				var newC = document.createElementNS(document.rootElement.namespaceURI,"circle"); 
				newC.setAttributeNS(null, "cx", activeDots[dot]);  
				newC.setAttributeNS(null, "cy", "0"); 
				newC.setAttributeNS(null, "class","hidden");
				newC.setAttributeNS(null, "r", 3);
				newC.setAttributeNS(null, "id", dot);
				svgDoc.getElementById("active-dots").appendChild(newC);
			}
			for (dot in activeDots){
				var newC = document.createElementNS(document.rootElement.namespaceURI,"circle"); 
				newC.setAttributeNS(null, "cx", activeDots[dot]);  
				newC.setAttributeNS(null, "cy", "0"); 
				newC.setAttributeNS(null, "class","enabled");
				newC.setAttributeNS(null, "r", 3);
				newC.setAttributeNS(null, "id", dot+"-inactive");
				svgDoc.getElementById("inactive-dots").appendChild(newC);
			}
			resetDefaults()
        };
  		function resetDefaults(){
  			for (dot in activeDots){
  				svgDoc.getElementById(dot).setAttribute('class','hidden');
  			}
  			svgDoc.getElementById("start-over").setAttribute('class','disabled');
  			svgDoc.getElementById("im-done").setAttribute('class','disabled');
  			for (dot in activeDots){drawnPoints[dot]=false}
  			drawLine();
  		}

		function select(evt){
			selectedPoint = 1;
		}

        function imDone(){
        	svgDoc.getElementById('im-done').setAttribute('class','disabled');
        }

        function draw(evt){
        	if (selectedPoint === null) {return}
			for (dot in activeDots){
				if (evt.clientX > activeDots[dot]-dotSpace & evt.clientX <= activeDots[dot]+dotSpace){
					svgDoc.getElementById(dot).setAttribute('class','enabled');
					svgDoc.getElementById(dot).setAttribute('cy',evt.clientY);
					drawnPoints[dot]=true;
				}
			}
			drawLine();
        };

        function isFinished(){
        	for (dot in activeDots){
        		if (drawnPoints[dot] === false){
        			return false;
        		}
        	}
        	return true;
        }

        function drawLine(){
        	var path = "M0,0 ";

        	for (dot in activeDots){
        		var dotEl = svgDoc.getElementById(dot);
        		var prevDot = Number(dot.split('-')[1])-1;
        		if (drawnPoints[dot] & drawnPoints['dot-'+prevDot]){
        			path = path+"L"+dotEl.getAttribute('cx')+","+dotEl.getAttribute('cy')+" ";
        		} else {
        			path = path+"M"+dotEl.getAttribute('cx')+","+dotEl.getAttribute('cy')+" ";
        		}

        	}
        	svgDoc.getElementById("active-line").setAttribute('d',path);

        }

		function newFish(){
			console.log('newfish')
			for (dot in activeDots){	
				svgDoc.getElementById(dot).setAttribute('id',dot+"-locked");
				svgDoc.getElementById(dot+"-inactive").setAttribute('id',dot);
			}
			svgDoc.getElementById("active-line").setAttribute('id',"locked-line");
			svgDoc.getElementById("inactive-line").setAttribute('id',"active-line");
		}

        function deselect(){
            selectedPoint = null;
            if (isFinished()){
        		svgDoc.getElementById("im-done").setAttribute('class','enabled');
        		svgDoc.getElementById("im-done").setAttribute('onmousedown', 'newFish()')
        	}
            for (dot in activeDots){
            	if (svgDoc.getElementById(dot).getAttribute('class') === 'enabled'){
            		svgDoc.getElementById("start-over").setAttribute('class','enabled');
            		svgDoc.getElementById("start-over").setAttribute('onmousedown', 'resetDefaults()')
            		return
            	}
            }
        };
